mapper.update:
update qhb_inf
set user_id = #{userId,jdbcType=INTEGER},
  user_ip = #{userIp,jdbcType=VARCHAR},
  qhb_time = #{qhbTime,jdbcType=TIMESTAMP},
  money = #{money,jdbcType=DOUBLE}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, user_id, user_ip, qhb_time, money
from qhb_inf
where id = #{id,jdbcType=INTEGER}
